public void readFromStream (InputStream in) throws IOException {
    byte buf [];
    int count;
    buf = new byte [16384];
    while (true) {
        count = in.read (buf);
        if (count < 0) break;

        this.write (buf, 0, count);
    }
}

public void readFromReader (Reader in) throws IOException {
    char buf [];
    int count;
    buf = new char [2048];
    while (true) {
        count = in.read (buf);
        if (count < 0) break;

        for (int i = 0;
        i < count; i ++) {
            this.write (buf [i]);
        }
    }
}

public AbstractFilter () {
    translationTable = latin1TranslationTable;
    specialsTable = noSpecialsTable;
}

public void write (int b) throws IOException {
    if (b < 0) b += 256;

    if (specialsTable [b]) writeSpecial (b);
    else {
        char ch = translationTable [b];
        if (ch != (char) 0) write (ch);

    }
}

public void write (byte [] buf, int off, int len) throws IOException {
    StringBuffer accumulator = null;
    while (len > 0) {
        short b = (short) buf [off];
        if (b < 0) b += 256;

        if (specialsTable [b]) {
            if (accumulator != null) {
                write (accumulator.toString ());
                accumulator = null;
            }
            writeSpecial (b);
        } else {
            char ch = translationTable [b];
            if (ch != (char) 0) {
                if (accumulator == null) accumulator = new StringBuffer ();

                accumulator.append (ch);
            }
        }
        len --;
        off ++;
    }
    if (accumulator != null) write (accumulator.toString ());

}

public void write (String s) throws IOException {
    int index, length;
    length = s.length ();
    for (index = 0; index < length; index ++) {
        write (s.charAt (index));
    }
}
